query Fund($fundId: ID!) {
  fund(fundId: $fundId) {
        _id
        name       
        manager {
          _id
          firstName
          lastName
          avatar
        }
        company {
          _id
          name
          avatar          
        }
      }
}

query FundManagers($featured: Boolean) {
  fundManagers(featured: $featured) {
      funds {
          _id
          name
          level
      }
      managers {
          _id
          firstName
          lastName
          avatar
          managedFundsIds
      }
  }
}

query FundCompanies {
    fundCompanies {
        _id
        name
        avatar
        postIds
        followerIds
        funds {
            _id
            name
        }
        fundManagers {
            _id
            firstName
            lastName
            avatar
        }
    }
}

query Professionals($featured: Boolean) {
  professionals(featured: $featured) {
    _id
    firstName
    lastName
    avatar
  }
}

query MentionUsers($search: String) {
    mentionUsers(search: $search) {
        _id
        firstName
        lastName
    }
}

query GlobalSearch($search: String) {
    globalSearch(search: $search) {
        users {
            _id
            firstName
            lastName
        }
        companies {
            _id
            name
        }
        posts {
            _id
            body
        }
        funds {
            _id
            name
        }
    }
}

query Posts($categories: [PostCategory!]) {
  posts(categories: $categories) {
      _id
      body
      media {
          url
          aspectRatio
      }
      mentionIds
      likeIds
      commentIds
      createdAt
      user {
          _id
          firstName
          lastName
      }
  }
}

query Account($featured: Boolean) {
    account {
        firstName
        lastName
        avatar
        role
        accreditation
        position
        companies {
            _id
            name
            avatar
        }
        posts(featured: $featured) {
            _id
        }
        invitees {
            __typename
            ... on User {
                email
            }
            ... on UserStub {
                email
            }
        }
    }
}

query UserProfile($userId: ID!) {
    userProfile(userId: $userId) {
        firstName
        lastName
        avatar
        position
        company {
            _id
            name
            avatar
        }
        companies {
            _id
            name
            avatar
        }
        linkedIn
        website
        twitter
    }
}

query CompanyProfile($companyId: ID!) {
    companyProfile(companyId: $companyId) {
        name
        avatar
        members {
            _id
            firstName
            lastName
            avatar
        }
    }
}

